package ejemplos;

public class _03VariablesLocales {

	static double calcularMedia(int x,int y , int z) {
		double media = (x + y + z)/3;
		return media;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 2;
		int b = 6;
		int c = 9;
		
		double media = calcularMedia(a,b,c);

	}

}
